INSERT INTO public.alternativas (id_alternativa, texto) VALUES (167, 'El prototipo = la tecnología en una forma que se puede utilizar para evaluar la viabilidad técnica y/o de fabricación o la utilidad del producto finalINSERT INTO public.alternativas (id_alternativa, texto) VALUES (168, '"El modelo representativo = una forma funcional de la tecnología');
