INSERT INTO public.alternativas (id_alternativa, texto) VALUES (176, 'Los primeros usuarios han demostrado que la tecnología completa funciona en operaciones realesINSERT INTO public.alternativas (id_alternativa, texto) VALUES (177, '"El desarrollo es continuo');
